import logo from './logo.svg';
import './App.css';
import api from './api';
import React, {Fragment, useEffect, useState} from 'react'
import * as events from "events";

function App() {
    const [passwords, setPasswords]= useState([])
    const [formData, setFormData] =useState({
        url: "",
        name: "",
        password: ""
    })
    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

    const getPasswords= async() => {
        const response = await api.get('/passwords/')
        setPasswords(response.data)
    }
    useEffect(() => {
        getPasswords();
    }, []);

    const handleChanges = (event)=>{
        const value = event.target.type ==='checkbox' ? event.target.checked : event.target.value;
        setFormData({
            ...formData,
        [event.target.name]: value
        });

    }
    const  handleSubmit = async (event) => {
        event.preventDefault();
        await api.post('/passwords/', formData);
        getPasswords();
        setFormData(
            {
                 url: "",
                name: "",
                password: ""
            }
        )
    }

  return (
      <div>
           <nav className="navbar navbar-dark, bg-primary" data-bs-theme="dark">
               <div className="container-fluid">
                   <a className='navbar-brand'> Docmetric Utility App
                   </a>
               </div>
           </nav>
           <div className="container">
            <form onSubmit={handleSubmit}>
                 <div className="mb-3 mt-3">
                    <label htmlFor='name' className='form-label'>
                        Name
                    </label>
                    <input type="text"  className="form-control" id="name" name="name" onChange={handleChanges} value={formData.name}>
                    </input>
                </div>
                <div className="mb-3 mt-3">
                    <label htmlFor='url' className='form-label'>
                        Url
                    </label>
                    <input type="text"  className="form-control" id="url" name="url" onChange={handleChanges} value={formData.url}>
                    </input>
                </div>

                 <div className="mb-3 mt-3">
                      <label htmlFor='password' className='form-label'>
                        Password
                    </label>
                      <input
                        type={showPassword ? 'text' : 'password'}
                        className="form-control"
                        id="password"
                        name="password"
                        placeholder="Enter your password"
                        onChange={handleChanges}
                        value={formData.password}

                      />
                      <div className="input-group-append">
                        <button type="button"  className="btn btn-warning" onClick={togglePasswordVisibility}>
                          {showPassword ? 'Hide' : 'Show'} Password
                        </button>

                      </div>
                 </div>

                <button type='submit' className="btn btn-primary">
                    Submit
                </button>
            </form>
            </div>
      </div>
  );
}

export default App;
