import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import {Container, Row, Col, Form, Button} from 'react-bootstrap';
import {syntaxHighlight} from "./sytaxHiglight";

function Beautifier() {

  const [textAreaValue, setTextAreaValue] = useState('');
  const [textAreaValueBeau, setTextAreaValueBeau] = useState('');

  // Handler function to update the state when the text area value changes
  const handleTextAreaChange = (event) => {
    setTextAreaValue(event.target.value);
  };

  // Function to handle form submission (you can do something with the textAreaValue here)
  const handleSubmit = (event) => {
    event.preventDefault();
    // You can access the textAreaValue here and perform actions like sending it to a server
      const beautifiedJSON = JSON.stringify(textAreaValue, null, 2);
      setTextAreaValueBeau(beautifiedJSON)

  };



  return (
    <Container className="mt-5">
      <Row>
        <Col md={6}>
          <Form.Group controlId="textArea1">
            <Form.Label>Text Area 1</Form.Label>
            <Form.Control
              as="textarea"
              rows={8}
              placeholder="Enter text here"
              style={{ overflowY: 'scroll' }}
              onChange={handleTextAreaChange}
            />
          </Form.Group>
        </Col>
        <div className=" mt-5">
      <Button size="md" variant="primary" onClick={handleSubmit}>
      Bautify
      </Button>
    </div>

        <Col md={6}>

            <div>

             <pre
        dangerouslySetInnerHTML={{
          __html: syntaxHighlight(JSON.stringify(textAreaValueBeau, undefined, 4))
        }}
      />
                </div>


        </Col>
      </Row>
    </Container>
  );
}

export default Beautifier;
